import React, { useRef, useLayoutEffect, useState, useEffect } from "react";
import styled, { css } from "styled-components";
import {
  FaBold,
  FaItalic,
  FaUnderline,
  FaLink,
  FaFolderOpen,
  FaTrash,
  FaBookmark,
  FaCog
} from "react-icons/fa";
import DocumentList from "components/DocumentList";
import useAutoSave from "components/Editor/useAutoSave";
import get from "lodash/get";
import { getDocumentById } from "api/document";
import moment from "moment";

const Inline = Quill.import("blots/inline");
const Header = Quill.import("formats/header");

Header.create = () => {
  return document.createElement("p");
};

class RelationshipBlot extends Inline {
  static create(value) {
    const node = super.create();
    node.setAttribute("data-relationship-target", value);
    return node;
  }

  static formats(node) {
    return node.getAttribute("data-relationship-target");
  }
}

RelationshipBlot.blotName = "relationship";
RelationshipBlot.tagName = "span";
RelationshipBlot.className = "blot-relationship";

Quill.register(RelationshipBlot);

const Panel = styled.div`
  height: 550px;
  width: 404px;
  min-width: 304px;
  flex-shrink: 0;
  position: absolute;
  bottom: 24px;
  right: 500px;
  z-index: 999999;
  background: white;
  box-shadow: 0 0 20px rgba(0, 0, 0, 0.14);
  overflow: hidden;
`;

const EditorContainer = styled.div`
  width: 100%;
  overflow: auto;
  height: 100%;

  .ql-container {
    font-family: inherit;
    padding-bottom: 44px;
  }

  .ql-editor {
    padding: 20px 20px;
    p {
      font-size: 16px;
      font-weight: normal;
    }

    h1 {
      font-size: 30px;
      font-weight: normal;
    }

    h2 {
      font-size: 21px;
      font-weight: normal;
    }

    .blot-relationship {
      color: red;
    }
  }
`;

const Meta = styled.div`
  color: #6e6e6e;
  font-size: 12px;
  ${"" /* line-height: 16px; */}
  padding-top: 4px;
  display: flex;
`;

const Container = styled.div`
  height: 100%;
  width: 100%;
  display: flex;
  flex-direction: column;
  position: relative;
`;

const TitleContainer = styled.div`
  width: 100%;
  padding: 20px 20px 16px;
  background: white;
  border-bottom: 1px solid #ebebeb;
`;

const Title = styled.input`
  width: 100%;
  border: none;
  color: #2c2c2c;
  outline: none;
  padding: 0;
  background: transparent;
`;

const FormatBar = styled.div`
  border-bottom: 1px solid #ebebeb;
  height: 44px;
  min-height: 44px;
  display: flex;
  align-items: center;
  padding: 0px 0px 0px 10px;
  user-select: none;

  button {
    transition: all 150ms;
    height: 100%;
    outline: none;

    width: 100%;
    outline: none;
    background: none;
    border: none;
    font-size: 12px;
    padding: 0px;
    margin: 0px;
    display: flex;
    align-items: center;
    justify-content: center;

    &:hover {
      color: #0070e0;
    }
    svg {
      display: block;
      padding: 0px;
      margin: 0px;
      max-width: 12px;
      max-height: 12px;
      min-width: 12px;
      min-height: 12px;
    }

    &.with-label {
      width: 100%;
      padding: 0px 12px;
      div {
        padding-left: 6px;
      }
    }
  }
`;

const SearchPane = styled.div`
  flex: 1;

  overflow: hidden;
  ${"" /* transition: all 250ms; */}
  box-shadow: 0px 0 20px rgba(0, 0, 0, 0.24);
  z-index: 10000;
  border-radius: 4px;
  height: 360px;

  background: white;
  position: fixed;
  left: 400px;
  top: 120px;
`;

const Overlay = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: rgba(0, 0, 0, 0.1);
  z-index: 1000;
`;

const DocumentPanel = ({ requestClose, ...props }) => {
  return (
    <Overlay onClick={requestClose}>
      <SearchPane className="handle" onClick={e => e.stopPropagation()}>
        <DocumentList {...props} />
      </SearchPane>
    </Overlay>
  );
};

const useSavedDocument = _id => {
  const [doc, setDoc] = useState(null);
  const [loading, setLoading] = useState(_id != null);

  useEffect(() => {
    if (_id) {
      (async () => {
        setLoading(true);
        const doc = await getDocumentById(_id);
        setDoc(doc);
        setLoading(false);
      })();
    } else {
      setLoading(false);
    }
  }, [_id]);

  return [loading, doc];
};

const useEditor = (element, doc, toolbarElement) => {
  const [, f] = useState(0);
  const editorRef = useRef(null);
  const [title, setTitle] = useState(doc ? doc.title : "");
  const [lastEditTime, setLastEditTime] = useState(null);

  const forceUpdate = () => f(s => s + 1);

  useLayoutEffect(() => {
    if (element && toolbarElement) {
      editorRef.current = new Quill(element.current, {
        modules: {
          toolbar: {
            container: toolbarElement.current
          }
        }
      });
      const editor = editorRef.current;

      editor.focus();

      editor.on("text-change", (o, n, source) => {
        if (source !== "api") {
          setLastEditTime(Date.now());
        }
      });

      forceUpdate();
    }
  }, []);

  useEffect(() => {
    if (editorRef.current && doc) {
      editorRef.current.setContents(doc.content);
      setTitle(doc.title);
    }
  }, [doc]);

  const updateTitle = t => {
    setTitle(t);
    setLastEditTime(Date.now());
  };

  const registerEdit = () => setLastEditTime(Date.now());

  const editor = editorRef.current;

  const content = editor ? editor.getContents() : null;
  const text = editor ? editor.getText() : "";
  const _id = doc ? doc._id : null;

  useAutoSave(_id, content, title, text, lastEditTime);

  const updatedAt =
    lastEditTime != null ? lastEditTime : get(doc, "updatedAt", null);
  const createdAt = get(doc, "createdAt", null);
  const currentDocument = {
    createdAt,
    updatedAt,
    content,
    text,
    title
  };

  return [editor, currentDocument, updateTitle, registerEdit];
};

const OpenDocumentPane = styled.div`
  width: 100%;
  background: white;
  height: 100%;
  position: absolute;
  top: 0px;
  z-index: 100000;
  transform: translate3d(0px, 100%, 0);

  transition: all 250ms;

  ${props =>
    props.open &&
    css`
      transform: translate3d(0px, 0, 0);
    `}
`;

const BottomBar = styled.div`
  height: 44px;
  width: 100%;
  flex-shrink: 0;
  border-top: 1px solid #ebebeb;

  position: absolute;
  bottom: 0px;
  right: 0px;
  display: flex;
  z-index: 1000;
`;

const Editor = ({ documentId: _id = null, setDocumentId = () => {} }) => {
  const [showLinkPanel, setShowLinkPanel] = useState(false);
  const [showOpenPanel, setShowOpenPanel] = useState(false);
  const el = useRef(null);
  const toolbarEl = useRef(null);
  const [loading, doc] = useSavedDocument(_id);

  const [
    editor,
    { title, createdAt, updatedAt },
    setTitle,
    registerEdit
  ] = useEditor(el, doc, toolbarEl);

  const stopKeys = {
    Enter: true,
    Tab: true
  };

  return (
    <Panel>
      <OpenDocumentPane open={showOpenPanel}>
        <DocumentList
          onCancel={() => setShowOpenPanel(false)}
          onDocumentSelect={id => {
            setDocumentId(id);
            setShowOpenPanel(false);
          }}
        />
      </OpenDocumentPane>
      {showLinkPanel && (
        <DocumentPanel
          requestClose={() => setShowLinkPanel(false)}
          onDocumentSelect={selectedDocument => {
            editor.format("relationship", selectedDocument);
            setShowLinkPanel(false);
            registerEdit();
          }}
        />
      )}
      <Container>
        <TitleContainer>
          <Title
            className="h2-quiet"
            placeholder="Untitled"
            value={title}
            onKeyDown={e => {
              if (stopKeys[e.key]) {
                e.preventDefault();
                e.stopPropagation();
                editor.focus();
              }
            }}
            onChange={e => {
              setTitle(e.target.value);
            }}
          />

          <Meta>
            <div className="detail">
              {updatedAt
                ? `Edited ${moment(updatedAt).format("MMM DD YYYY HH:MM A")}`
                : "New Draft"}
            </div>
          </Meta>
        </TitleContainer>
        <FormatBar ref={toolbarEl}>
          <button className="ql-bold">
            <FaBold />
          </button>
          <button className="ql-italic">
            <FaItalic />
          </button>
          <button className="ql-underline">
            <FaUnderline />
          </button>
        </FormatBar>
        <EditorContainer className="body">
          <div ref={el} />
        </EditorContainer>
      </Container>
      <BottomBar>
        <FormatBar>
          <button
            onClick={e => {
              e.preventDefault();
              e.stopPropagation();
              setShowOpenPanel(true);
            }}
            className="with-label"
          >
            <FaFolderOpen />
            <div>Open</div>
          </button>
          <button className="with-label">
            <FaLink />
            <div>Relationship</div>
          </button>
          <button className="with-label">
            <FaTrash />
            <div>Delete</div>
          </button>
        </FormatBar>
      </BottomBar>
    </Panel>
  );
};

export default Editor;
