import React, { useRef } from "react";
import { useSavedDocument, useEditor } from "./hooks";
import styled from "styled-components";

const Container = styled.div`
  flex: 1;
  overflow: hidden;

  .ql-container {
    font-family: inherit;
    padding: 0px;

  }

  .ql-editor {

    padding: 16px 16px 0px;
    border-bottom: 16px solid transparent;
    p {
      font-size: 16px;
      font-weight: normal;
    }

    h1 {
      font-size: 30px;
      font-weight: normal;
    }

    h2 {
      font-size: 21px;
      font-weight: normal;
    }

    .blot-relationship {
      color: red;
    }
  }
`;

const Editor = ({ editorRef }) => {
  return (
    <Container>
      <div ref={editorRef} />
    </Container>
  );
};

export default Editor;
