import React, { useRef, useLayoutEffect, useState, useEffect } from "react";
import useAutoSave from "components/Editor/useAutoSave";
import get from "lodash/get";
import { getDocumentById } from "api/document";
import moment from "moment";

const Inline = Quill.import("blots/inline");
const Header = Quill.import("formats/header");

Header.create = () => {
  return document.createElement("p");
};

class RelationshipBlot extends Inline {
  static create(value) {
    const node = super.create();
    node.setAttribute("data-relationship-target", value);
    return node;
  }

  static formats(node) {
    return node.getAttribute("data-relationship-target");
  }
}

RelationshipBlot.blotName = "relationship";
RelationshipBlot.tagName = "span";
RelationshipBlot.className = "blot-relationship";

Quill.register(RelationshipBlot);

export const useSavedDocument = _id => {
  const [doc, setDoc] = useState(null);
  const [loading, setLoading] = useState(_id != null);

  useEffect(() => {
    if (_id) {
      (async () => {
        setLoading(true);
        const doc = await getDocumentById(_id);
        setDoc(doc);
        setLoading(false);
      })();
    } else {
      setLoading(false);
    }
  }, [_id]);

  return [loading, doc];
};

export const useEditor = (element, doc, toolbarElement) => {
  const [, f] = useState(0);
  const editorRef = useRef(null);
  const [title, setTitle] = useState(doc ? doc.title : "");
  const [lastEditTime, setLastEditTime] = useState(null);

  const forceUpdate = () => f(s => s + 1);

  useLayoutEffect(() => {
    if (element.current && toolbarElement) {
      editorRef.current = new Quill(element.current, {
        modules: {
          toolbar: {
            container: toolbarElement.current
          }
        }
      });
      const editor = editorRef.current;

      editor.focus();

      editor.on("text-change", (o, n, source) => {
        if (source !== "api") {
          setLastEditTime(Date.now());
        }
      });

      forceUpdate();
    }
  }, []);

  useEffect(() => {
    if (editorRef.current && doc) {
      editorRef.current.setContents(doc.content);
      setTitle(doc.title);
    }
  }, [doc]);

  const updateTitle = t => {
    setTitle(t);
    setLastEditTime(Date.now());
  };

  const registerEdit = () => setLastEditTime(Date.now());

  const editor = editorRef.current;

  const content = editor ? editor.getContents() : null;
  const text = editor ? editor.getText() : "";
  const _id = doc ? doc._id : null;

  useAutoSave(_id, content, title, text, lastEditTime);

  const updatedAt =
    lastEditTime != null ? lastEditTime : get(doc, "updatedAt", null);
  const createdAt = get(doc, "createdAt", null);
  const currentDocument = {
    createdAt,
    updatedAt,
    content,
    text,
    title
  };

  return [editor, currentDocument, updateTitle, registerEdit];
};
